  selector: 'app-drink-list',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './drink-list.component.html',
  styleUrls: ['./drink-list.component.css']
})
export class DrinkListComponent {
  //@Input
  @Input() cardLocation!:CardLocation;
}
