import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  standalone: true,
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {
  let cartItemCount = 0;

  // Fun√ß√£o para adicionar itens ao carrinho
  function addToCart() {
    cartItemCount++; // Incrementa o n√∫mero de itens
    document.getElementById('cart-count').textContent = cartItemCount; // Atualiza o contador no bot√£o do carrinho
  }

  // Exemplo: Adicionando 3 itens ao carrinho
  addToCart();
  addToCart();
  addToCart();
}
